require ["fileinto", "variables", "vnd.dovecot.extdata"];

if allof (header :is "X-Spam-Status" "Yes",
  extdata :is "discard_spam" "yes") {
  discard;
} else {
  fileinto "Spam";
}

require ["fileinto", variables", "vnd.dovecot.extdata"];

if allof (header :is "X-Spam-Status" "Yes",
  string :is "${extdata.discard_spam}" "yes") {
  discard;
} else {
  fileinto "Spam";
}
